import axios from 'axios';
import { useNavigate } from 'react-router-dom'; // Importez useNavigate à partir de react-router-dom
import {
  USER_LOGIN_SUCCESS,
  USER_LOGIN_FAIL,
  USER_LOGOUT,
  USER_PROFILE_SUCCESS,
  USER_PROFILE_FAIL,
  USER_PROFILE_RESET,
  USER_PROFILE_UPDATE,
  USER_PROFILE_UPDATE_FAIL,
} from '../constants/actions';
import { API_BASE_URL } from '../constants/url';

const baseUrl = API_BASE_URL;

// Action de connexion
export const login = (email, password) => async (dispatch) => {
  try {
    const config = {
      headers: {
        'Content-type': 'application/json',
      },
    };

    const { data } = await axios.post(
      `${baseUrl}user/login`,
      { email, password },
      config
    );

    dispatch(loginSuccess(data));
    dispatch(userProfile(data.body.token));
  } catch (error) {
    console.log('Error in login:', error);
    dispatch({
      type: USER_LOGIN_FAIL,
      payload:
        error.response && error.response.data.message
          ? error.response.data.message
          : error.message,
    });
  }
};

// Action de succès de connexion
export const loginSuccess = (data) => ({
  type: USER_LOGIN_SUCCESS,
  payload: data,
});

// Action de déconnexion
export const logout = () => async (dispatch) => {
  const navigate = useNavigate(); // <- Utilisation incorrecte de useNavigate

  try {
    dispatch(userLogout());
    dispatch(userProfileReset());

    navigate('/loginForm');
  } catch (error) {
    console.error('Error in logout:', error);
  }
};


// Action de déconnexion de l'utilisateur
export const userLogout = () => ({ type: USER_LOGOUT });

// Action de réinitialisation du profil utilisateur
export const userProfileReset = () => ({ type: USER_PROFILE_RESET });

// Action de profil utilisateur réussie
export const userProfile = (token) => async (dispatch) => {
  try {
    const { data } = await axios.post(
      `${baseUrl}user/profile`,
      { token },
      getConfig(token)
    );
    dispatch({ type: USER_PROFILE_SUCCESS, payload: data });
  } catch (error) {
    dispatch(handleError(USER_PROFILE_FAIL, error));
  }
};

// Action de mise à jour du profil utilisateur
export const updateProfile = (token, newFirstName, newLastName) => async (
  dispatch
) => {
  try {
    const { data } = await axios.put(
      `${baseUrl}user/profile`,
      { firstName: newFirstName, lastName: newLastName },
      getConfig(token)
    );
    dispatch(userProfileUpdate(data));
  } catch (error) {
    dispatch(handleError(USER_PROFILE_UPDATE_FAIL, error));
  }
};

// Action de mise à jour du profil utilisateur réussie
export const userProfileUpdate = (data) => ({
  type: USER_PROFILE_UPDATE,
  payload: data,
});

// Fonction pour gérer les erreurs
const handleError = (type, error) => ({
  type,
  payload:
    error.response && error.response.data.message
      ? error.response.data.message
      : error.message,
});

// Fonction pour obtenir la configuration avec le token d'authentification
const getConfig = (token) => ({
  headers: {
    'Content-Type': 'application/json',
    Authorization: `Bearer ${token}`,
  },
});
